import React from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { renderRoutes } from "react-router-config";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";

// Internal imports
import history from "./Store/history/index";
import routes from "./Routes/Routes";
import PrivateRoute from "./Routes/PrivateRoutes";
import LoginRoute from "./Routes/LoginRoute";
// import ToastMessage from './Components/ToastMessage';
import Loader from './Components/Loader';
// import LoginModal from './Components/Modal/LoginModal/LoginModal';
// import LocationModal from './Components/Modal/LocationModal/LocationModal';
// import FinetuneLocation from './Components/Modal/FinetuneLocation';
import { closeLogin,closeSignup ,openLogin ,openSignup, needGuestCartSyncFinish, needGuestCartSyncReset } from './Store/action/login';
import { getUserProfile, clearCartData, deleteUserCart } from './Store/action/user'
import { closeLocation, getDeliveryRange, openFinetuneLocation, closeFinetuneLocation, setUserLocation,checkDeliveryRequest } from './Store/action/location';
// import { showToastMessage, resetToastMessage, setStartLoader, setStopLoader } from './Store/action/loader';


// Style imports
import "./App.css";
import "./index.css";
import SignupModal from "./Components/Modal/SignupModal/SignupModal";
import ReactGA from 'react-ga';
import ReactPixel from 'react-facebook-pixel';
const options = {
  autoConfig: true, // set pixel's autoConfig
  debug: false, // enable logs
};

ReactGA.initialize(process.env.REACT_APP_GOOGLE_ANALYTICS_TRACKING_ID);
ReactGA.pageview(window.location.pathname + window.location.search);
ReactPixel.init('866509970611036', options);
ReactPixel.pageView(window.location.pathname + window.location.search); // For tracking page view
ReactPixel.trackCustom('ViewContent',  { content_ids: ['partner_event_id'], eventref: '' // or set to empty string
});
// ReactPixel.trackCustom('AddToCart',  { content_ids: ['partner_event_id'], eventref: '' // or set to empty string
// });
// ReactPixel.trackCustom('Search',  { content_ids: ['partner_event_id'], eventref: '' // or set to empty string
// });
// ReactPixel.trackCustom('InitiateCheckout',  { content_ids: ['partner_event_id'], eventref: '' // or set to empty string
// });
// ReactPixel.trackCustom('AddPaymentInfo',  { content_ids: ['partner_event_id'], eventref: '' // or set to empty string
// });
// ReactPixel.trackCustom('Purchase',    {
//   content_ids: ['partner_event_id'],
//   eventref: 'fb_oea', // or set to empty string
//   currency: 'QAR' , // your currency string value goes here
//   num_items: 1, // your number of tickets purchased value goes here
//   value: 15.30, // your total transaction value goes here
// });

const trackPage = (page) => {
  ReactGA.set({
    page,

  });
  ReactGA.pageview(page);
};

const fbPixelPageView=(page)=>{
  ReactPixel.pageView(page); // For tracking page view
}



const resolvePrivateRoutes = (routes) => {

  if (routes && Array.isArray(routes)) {
    console.log(routes,"routes")

    return routes.map((route) => {
      console.log(route,"routes")

      if (route.isPrivate) {
        route.render = (props) => (
          <PrivateRoute trackPage={trackPage} fbPixelPageView={fbPixelPageView} component={route.component} {...props} />
        );
      }
      if (route.isAuth) {
        route.render = (props) => (
          <LoginRoute  trackPage={trackPage} fbPixelPageView={fbPixelPageView} component={route.component} {...props} />
        );
      }
      if (route.routes) {
        resolvePrivateRoutes(route.routes);
      }
      return route;
    });
  }
};

function App(props) {
  const { 
    toast,
    openLogin, 
    loaderStatus, 
    openSignup,

    showLogin,
    showSignup,
    closeSignup, 
    showToastMessage, 
    resetToastMessage, 
    closeLogin, 
    location, 
    closeLocation, 
    getDeliveryRange, 
    openFinetuneLocation,
    closeFinetuneLocation,
    setStartLoader, 
    setStopLoader,
    setUserLocation,
    checkDeliveryRequest,
    deliveryData,
    deliveryDataError,
    getUserProfile,
    userProfile,
    needGuestCartSync,
    needGuestCartSyncFinish,
    needGuestCartSyncReset,
    clearCartData,
    deleteUserCart
  } = props;
  return (

    <Router history={history} basename={process.env.PUBLIC_URL}>

      <ToastMessage openLogin={openLogin}toastData={toast && toast} />

      <LoginModal show={showLogin} showToastMessage={showToastMessage} resetToastMessage={resetToastMessage} closeLogin={closeLogin} openSignup={openSignup} openLogin={openLogin} needGuestCartSync={needGuestCartSync} needGuestCartSyncFinish={needGuestCartSyncFinish} needGuestCartSyncReset={needGuestCartSyncReset} />
      <SignupModal show={showSignup} userProfile={userProfile} showToastMessage={showToastMessage} resetToastMessage={resetToastMessage} closeSignup={closeSignup} openLogin={openLogin} getUserProfile={getUserProfile} needGuestCartSync={needGuestCartSync} needGuestCartSyncFinish={needGuestCartSyncFinish} needGuestCartSyncReset={needGuestCartSyncReset}/>
      <LocationModal show={location.showLocation} setUserLocation={setUserLocation} showToastMessage={showToastMessage} resetToastMessage={resetToastMessage} closeLocation={closeLocation} getDeliveryRange={getDeliveryRange} openFinetuneLocation={openFinetuneLocation} deliverStatus={location.deliveryRange && location.deliveryRange.delivery_status} setStartLoader={setStartLoader} setStopLoader = {setStopLoader}/>
      <FinetuneLocation deleteUserCart={deleteUserCart} clearCartData={clearCartData} show={location.showFinetuneLocation} setUserLocation={setUserLocation} showToastMessage={showToastMessage} resetToastMessage={resetToastMessage} closeLocation={closeLocation} getDeliveryRange={getDeliveryRange} closeFinetuneLocation={closeFinetuneLocation} deliverStatus={location.deliveryRange && location.deliveryRange.delivery_status} deliveryData={deliveryData} deliveryDataError={deliveryDataError}  checkDeliveryRequest={ checkDeliveryRequest} />
      <Loader show={loaderStatus}/>
      {renderRoutes(resolvePrivateRoutes(routes))}
    </Router>
  );
}

const mapStateToProps = (state) => {
  return {
    toast: state.loader.toast,
    loaderStatus: state.loader.loaderStatus,
    showLogin : state.login.showLogin,
    showSignup : state.login.showSignup,
    location: state.location,
    deliveryData:state.location.deliveryData,
    deliveryDataError:state.location.deliveryDataError,
    userProfile:state.user.userProfile,
    needGuestCartSync: state.login.needGuestCartSync
  }
};

function mapDispatchToProps(dispatch) {
  return bindActionCreators({
    openLogin,
    closeLogin,
    openSignup,
    closeSignup,
    closeLocation,
    showToastMessage,
    resetToastMessage,
    getDeliveryRange,
    openFinetuneLocation,
    closeFinetuneLocation,
    setStartLoader,
    setStopLoader,
    setUserLocation,
    checkDeliveryRequest,
    getUserProfile,
    needGuestCartSyncFinish,
    needGuestCartSyncReset,
    clearCartData,
    deleteUserCart
  }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
